    private void BtStartActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BtStartActionPerformed
        double zahl;
        String zahlStr;

        zahlStr = TfZahl.getText();
        zahl = Double.parseDouble(zahlStr);

        tuerme.put("a", TfStart.getText());
        tuerme.put("a", TfMitte.getText());
        tuerme.put("a", TfZiel.getText());

        umbauen(zahl, "a", "b", "c");
    }//GEN-LAST:event_BtStartActionPerformed

    public void umbauen(double zahl, String quelle, String temp, String ziel) {
        if (zahl > 0) {
            umbauen(zahl - 1, quelle, ziel, temp);

            String quelleString = tuerme.get(quelle);
            String scheibe = quelleString.substring(quelleString.length() - 1);
            quelleString = quelleString.substring(0, quelleString.length() - 1);
            tuerme.put(quelle, quelleString);

            String zielString = tuerme.get(ziel);
            zielString = zielString.concat(scheibe);
            tuerme.put(ziel, zielString);

            umbauen(zahl - 1, temp, ziel, quelle);
        }
        System.out.println(tuerme);
//    if size > 0:
//        versetze(size-1, quelle=quelle, ziel=temp, temp=ziel)
//        quelle.pop()
//        ziel.push()
//        versetze(size-1, quelle=temp, ziel=ziel, temp=quelle)
    }

